import java.util.Collections;public class AddTwoLinkedList {    public static void main(String[] args) {        LinkedList list1 = new LinkedList();        list1.addList(1);        list1.addList(6);        list1.addList(5);        LinkedList list2 = new LinkedList();        list2.addList(1);        list2.addList(6);        list2.addList(5);        LinkedList.Node l1 = list1.head;        LinkedList.Node l2 = list2.head;        l1 = reverse(l1);        l2 =reverse(l2);        LinkedList.Node res = addTwoList(l1, l2);    }    public static LinkedList.Node reverse(LinkedList.Node l){        LinkedList.Node cur = l;        LinkedList.Node prev = null;        LinkedList.Node next = null;        while(cur != null){            next = cur.next;            cur.next = prev;            prev = cur;            cur = next;        }        return prev;    }    public static LinkedList.Node addTwoList(LinkedList.Node l1, LinkedList.Node l2){        int carry = 0;        LinkedList list = new LinkedList();        LinkedList.Node dummy = l1;        dummy.val = 0;        System.out.println(dummy+" "+dummy.val+" "+dummy.next);        return null;    }}