import java.util.HashMap;import java.util.Scanner;public class AnagramDetect {    public static boolean bothAreAnagram(String str1, String str2){        HashMap<Character, Integer> map = new HashMap<>();        for(char ch : str1.toCharArray())            map.put(ch, map.getOrDefault(ch, 0)+1);        for(char ch : str2.toCharArray())            map.put(ch, map.getOrDefault(ch, 0) - 1);        for(char key : map.keySet()){            if(map.get(key) < 0)                return false;        }        return true;    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Enter Your Original String");        String original = sc.next();        System.out.println("Enter Your another String");        String another = sc.next();        if(bothAreAnagram(original, another))            System.out.println("Both are Anagram of each other");        else            System.out.println("Not anagram strings");    }}