import java.util.Arrays;import java.util.Scanner;public class BubbleSort {    public static void bubbleSorting(int[] arr, int n){        for(int i = 0; i < n; i++){            for(int j = 0; j < n - i -1; j++){                if(arr[j] > arr[j + 1]){                    swap(arr, j, j+1);                }            }        }        System.out.println(Arrays.toString(arr));    }    public static void swap(int[] arr, int j, int i){        int temp = arr[j];        arr[j] = arr[i];        arr[i] = temp;    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        for(int i = 0; i < n; i++){            arr[i] = sc.nextInt();        }        bubbleSorting(arr, n);    }}