import java.util.Scanner;public class ContainerArea {    public static void main(String[] args) {        Scanner sc =  new Scanner(System.in);          int n = sc.nextInt();          int[] arr = new int[n];          for(int i = 0; i < n; i++){              arr[i] = sc.nextInt();          }          maxArea(arr);    }    public static void maxArea(int[] arr){        int maxArea = 0;        int i = 0;        int j = arr.length - 1;        while(i < j){            maxArea = Math.max(maxArea, Math.min(arr[i], arr[j])* (j - i));            if(arr[i] < arr[j])                i++;            else                j--;        }        System.out.println(maxArea);    }}