import java.util.Scanner;public class IntegerConversion {    public static int fastPower(int num, int pow){        if(pow == 0){            return 1;        }        int small = fastPower(num, pow/2);        small = small * small;        if(pow % 2 != 0)            small = num * small;        return small;    }    public static void convertToInteger(String binary){        int j = 0, num = 0;        for(int i = binary.length() - 1; i >= 0; i--)        {            if(binary.charAt(i) == '1')                num += fastPower(2,j);            j++;        }        System.out.println(num);    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Enter binary of any number");        String binary = sc.next();        convertToInteger(binary);    }}