import java.util.Scanner;public class MatrixAntiClock90Deg {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[][] arr = new int[n][n];        for(int i = 0; i < n; i++){            for(int j = 0; j < n; j++){                arr[i][j] = sc.nextInt();            }        }        rotateMatrix(arr, n);    }    public static void rotateMatrix(int[][] arr, int n){        for(int i = 0; i < (n / 2); i++){            for(int j = i; j < n - i - j; j++){                int temp = arr[i][j];                arr[i][j] = arr[j][n - i - 1];                arr[j][n - i - 1] = arr[n - i - 1][n - j - 1];                arr[n - i - 1][n - j - 1] = arr[n - j - 1][i];                arr[n - j - 1][i] = temp;            }        }        for(int i = 0; i < n; i++){            for(int j = 0; j < n; j++){                System.out.print(arr[i][j]+" ");            }            System.out.println();        }    }}