import java.util.Arrays;import java.util.Scanner;import java.util.Stack;public class NearestSmallToLeft {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int size = n;        int[] arr = new int[n];        int i = 0;        while(n-- > 0){            arr[i++] = sc.nextInt();        }        leftSmall(arr, size);    }    public static void leftSmall(int[] arr, int n){        Stack<Integer> st = new Stack<>();        int[] res = new int[n];        for(int i = 0; i < n; i++){            while(!st.isEmpty() && st.peek() >= arr[i])                st.pop();            if(st.isEmpty())                res[i] = -1;            else                res[i] = st.peek();            st.push(arr[i]);        }        System.out.println(Arrays.toString(res));    }}