import java.util.Scanner;public class NumberOfIsland {    public static void findIslands(int[][] matrix, int n, int m){        int count = 0;        for(int i = 0; i <n; i++){            for(int j = 0; j < m; j++){                if(matrix[i][j] == 1){                    count++;                    makeWater(matrix, i, j);                }            }        }        System.out.println(count);    }    public static void makeWater(int[][] matrix, int row, int col){        if(row < 0 || row >= matrix.length || col <0 || col >= matrix[0].length || matrix[row][col] == 0)            return;        matrix[row][col] = 0;        makeWater(matrix, row + 1, col);        makeWater(matrix, row - 1, col);        makeWater(matrix, row, col + 1);        makeWater(matrix, row, col - 1);        return;    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int m = sc.nextInt();        int[][] matrix = new int[n][m];        for(int i = 0; i < n; i++){            for(int j = 0; j < m; j++){                matrix[i][j] = sc.nextInt();            }        }        findIslands(matrix, n, m);    }}