import java.util.Scanner;public class PalindromeDetect {    public static boolean isPalindrome(String word){        char[] arr = word.toCharArray();        StringBuilder reverse = new StringBuilder();        for(int i = arr.length - 1; i >= 0; i--){            reverse.append(arr[i]);        }        return word.equals(reverse.toString());    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Enter Your word String");        String word = sc.next();        if(isPalindrome(word))            System.out.println("Palindrome");        else            System.out.println("Not a Palindrome");    }}