import java.util.Scanner;public class PowerOfTwo {    public static  int fastPower(int x, int n){        if(n == 0)            return 1;        int small = fastPower(x, n/2);        small *= small;        if(n % 2 != 0)            small = small * x;        return small;    }    public static boolean powerOfTwo(int num){        int low = 1, high = num;        while(low <= high){            int mid = low + (high - low)/2;            if(fastPower(2,mid) == num)                return true;            else if (mid * mid > num)                high = mid - 1;            else                low = mid + 1;        }        return false;    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Enter Your number");        int num = sc.nextInt();        if(powerOfTwo(num))            System.out.println("Yes");        else            System.out.println("No");    }}