import java.util.Arrays;import java.util.Scanner;public class QuickSorting {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        for(int i = 0; i < n; i++)            arr[i] = sc.nextInt();        quickSort(arr, 0, n - 1);        System.out.println(Arrays.toString(arr));    }    public static void  quickSort(int[] arr, int low, int high){        if(low < high) {            int p_index = partition(arr, low, high);            quickSort(arr, low, p_index - 1);            quickSort(arr, p_index + 1, high);        }    }    public static int partition(int[] arr, int low, int high){        int p_index = low;        int pivot = arr[high];        for(int i = low; i < high; i++){            if(arr[i] <= pivot){                int temp = arr[i];                arr[i] = arr[p_index];                arr[p_index] = temp;                p_index++;            }        }        arr[high] = arr[p_index];        arr[p_index] = pivot;        return p_index;    }}