import java.util.Arrays;import java.util.Scanner;public class SortingZeroOneTwo {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        for(int i = 0; i < n; i++){            arr[i] = sc.nextInt();        }        sortArray(arr);    }    public static void sortArray(int[] arr){        int ptr = 0;        int i = 0;        int j = arr.length - 1;        while(i <= j){            if(arr[i] == 0){               int temp = arr[i];               arr[i] = arr[ptr];               arr[ptr] = temp;               ptr++;               i++;            }            else if(arr[i] == 1)                i++;            else{                int temp = arr[j];                arr[j] = arr[i];                arr[i] = temp;                j--;            }        }        System.out.println(Arrays.toString(arr));    }}