import java.util.Arrays;public class StackUsingArray {    private final long[] arr;    private int top;    public StackUsingArray(int size){        this.arr = new long[size];        this.top = -1;    }    public void pushElement(int val){        top = top + 1;        arr[top] = val;    }    public long topElement(){        if(top < 0) return -1;        return arr[top];    }    public long poppedElement(){        if(top < 0) throw new NullPointerException("Empty stack");        return arr[top--];    }    public boolean empty(){        if(top < 0)            return true;        return false;    }    public int printSize(){        return top+1;    }    public void display(){        System.out.println(Arrays.toString(arr));    }    public static void main(String[] args) {        StackUsingArray stack = new StackUsingArray(5);        System.out.println(stack.printSize());        stack.pushElement(1);        stack.pushElement(1);        stack.pushElement(1);        System.out.println(stack.poppedElement());        stack.pushElement(1);        stack.pushElement(1);        stack.display();        System.out.println(stack.printSize());    }}